#!/usr/bin/pythonimport os, sys, socket, rehost = os.environ['HM_SRV_IPADDR']port = int(os.environ['HM_SRV_PORT'])send_string = sys.argv[1]send_string += "\r\n"receive_string = sys.argv[2]try:   s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)   s.connect((host, port))   s.send(send_string)   response = s.recv(8192)except:   sys.exit(-1)if(None != re.search(r"%s" % receive_string, response)):   sys.exit(0)else:   sys.exit(-1)